def create_unigram(words):
    assert type(words) == list
    return words

#bigram
def create_bigrams(words):
    assert type(words) == list
    gap = 0
    joined_str = " "
    l = len(words)
    if l > 1:
        lst = []
        for i in range(l-1):
            for k in range(1,gap+2):
                if i+k < l:
                    lst.append(joined_str.join([words[i],words[i+k]]))
    else:
        #set it as unigram
        lst = create_unigram(words)
    return lst


# In[ ]:


def create_trigrams(words):
    assert type(words) == list
    gap == 0
    joined_str = " "
    l = len(words)
    if l > 2:
        lst = []
        for i in range(1,gap+2):
            for k1 in range(1, gap+2):
                for k2 in range(1,gap+2):
                    if i+k1 < l and i+k1+k2 < l:
                        lst.append(joined_str.join([words[i], words[i+k1],words[i+k1+k2]]))
        else:
            #set is as bigram
            lst = create_bigram(words)
    return lst


